 
module.exports = 
{

	calcSoma: function(valor1, valor2)
	{
		return parseFloat(valor1)+parseFloat(valor2);
	},

    calcSubtr: function(valor1, valor2)
	{
		return parseFloat(valor1)-parseFloat(valor2);
	},

    calcMultip: function(valor1, valor2)
	{
		return parseFloat(valor1)*parseFloat(valor2);
	},

    calcDivide: function(valor1, valor2)
	{
		return parseFloat(valor1)/parseFloat(valor2);
	}

}